    //     }

    // }

    const titleChangeHandler = (event)=>{
        // console.log(event.target.value);
        // setTitle(event.target.value)
        setUserInput({
            ...userInput
        })

    }
    const amoutChangeHandler = (event) =>{
        setAmout(event.target.value)
    }
    const dateChangeHandler = (event) =>{
        setDate(event.target.value)
    }
    const SubmitHandler = (event)=>{
         event.preventDefault()
        console.log(title);
        console.log(amout);
        console.log(date);

    }
    return(
       <form onSubmit={SubmitHandler}>
           <div className='new-expense__controls'>
               <div className='new-expense__control'>
                   <label>Title</label>
                   <input name='title' type="text" onChange={titleChangeHandler}/>
               </div >
               <div className='new-expense__control'>
               <label>Amout</label>
                   <input name='amout' type="number" min="0.1" step="1" onChange={amoutChangeHandler}/>
               </div>
               <div className='new-expense__control'>
               <label>Date</label>
                   <input name='date' type="date" min="2022-01-01" onChange={dateChangeHandler}/>
               </div>
           </div>
           <div className='new-expense__actions'>
               <button type='submit'>Add Expense</button>

           </div>

       </form>

    )
}
export default ExpenseForm;
