    // }
    // const amoutChangeHandler = (event) =>{
    //     setAmout(event.target.value)
    // }
    // const dateChangeHandler = (event) =>{
    //     setDate(event.target.value)
    // }
    const SubmitHandler = (event)=>{
         event.preventDefault()
        console.log(title);
        console.log(amout);
        console.log(date);

    }
    return(
       <form onSubmit={SubmitHandler}>
           <div className='new-expense__controls'>
               <div className='new-expense__control'>
                   <label>Title</label>
                   <input name='title' type="text" onChange={inputChangeHandler}/>
               </div >
               <div className='new-expense__control'>
               <label>Amout</label>
                   <input name='amout' type="number" min="0.1" step="1" onChange={inputChangeHandler}/>
               </div>
               <div className='new-expense__control'>
               <label>Date</label>
                   <input name='date' type="date" min="2022-01-01" onChange={inputChangeHandler}/>
               </div>
           </div>
           <div className='new-expense__actions'>
               <button type='submit'>Add Expense</button>

           </div>

       </form>

    )
}
export default ExpenseForm;
